mapper.insert:

insert into file_info (file_id, user_id, file_name, 
  lre_result, status, create_time
  )
values (#{fileId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{fileName,jdbcType=VARCHAR}, 
  #{lreResult,jdbcType=TINYINT}, #{status,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:

select file_id, user_id, file_name, lre_result, status, create_time
from file_info
where file_id = #{fileId,jdbcType=INTEGER}


select file_id, file_name, lre_result, status, create_time
from file_info
where 1 = 1

  and user_id = #{fileInfo.userId,jdbcType=INTEGER}


  and file_name like concat('%',#{fileInfo.fileName},'%')


  and lre_result = #{fileInfo.lreResult,jdbcType=TINYINT}


  and status = #{fileInfo.status,jdbcType=TINYINT}



    and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%S') >=  DATE_FORMAT(#{fileInfo.startTime}, '%Y-%m-%d %H:%i:%S')
    and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%S') <=  DATE_FORMAT(#{fileInfo.endTime}, '%Y-%m-%d %H:%i:%S')


order by create_time desc
