mapper.update:

update lre_server.session_info
set client_id = #{clientId,jdbcType=VARCHAR},
  session_content = #{sessionContent,jdbcType=VARCHAR},
  lre_result = #{lreResult,jdbcType=TINYINT},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where session_id = #{sessionId,jdbcType=VARCHAR}

mapper.select:

select session_id, client_id, session_content, lre_result, create_time
from lre_server.session_info
where session_id = #{sessionId,jdbcType=VARCHAR}
